Analytics.Script:
create temporary table ServiceRequestData USING CarbonAnalytics OPTIONS(tableName "ORG_WSO2_CARBON_MSS_HTTPMONITORING");
CREATE TEMPORARY TABLE MSS_REQUESTS_SUMMARY_PER_MINUTE USING CarbonAnalytics OPTIONS (tableName "MSS_REQUESTS_SUMMARY_PER_MINUTE", schema "service_class string, application_type string, server_address string, avg_request_count int, avg_response_time int, http_success_count int, http_error_count int, session_count int, time string, group_by FACET -i", primaryKeys "service_class,server_address,time" );
insert into table MSS_REQUESTS_SUMMARY_PER_MINUTE select service_class, meta_application_type as application_type, meta_server_addess as server_addess, count(request_uri) as avg_request_count, avg(response_time) as avg_response_time, sum(if(http_status_code<400, 1, 0)) as http_success_count, sum(if(http_status_code>=400, 1, 0)) as http_error_count, 0 as session_count, substring(cast(meta_timestamp/1000 as timestamp),0,16) as time, facet2(service_class,server_address) from ServiceRequestData group by meta_application_type, meta_server_addess, service_class, substring(cast(meta_timestamp/1000 as timestamp),0,16);
CREATE TEMPORARY TABLE MSS_HTTP_STATUS USING CarbonAnalytics OPTIONS (tableName "MSS_HTTP_STATUS", schema "service_class string, server_address string, avg_request_count int, http_status_code int, time string, group_by FACET -i", primaryKeys "service_class,server_address,time" );
insert into table MSS_HTTP_STATUS select service_class, meta_server_addess as server_addess, count(request_uri) as avg_request_count, http_status_code, substring(cast(meta_timestamp/1000 as timestamp),0,16) as time, facet2(service_class,server_address) from ServiceRequestData group by meta_server_addess, service_class, http_status_code, substring(cast(meta_timestamp/1000 as timestamp),0,16);
CREATE TEMPORARY TABLE MSS_SERVICE_CONTEXT_FINAL USING CarbonAnalytics OPTIONS (tableName "MSS_SERVICE_CONTEXT", schema "service_class string, server_address string, avg_request_count int, service_context string, time string", group_by FACET -i", primaryKeys "service_class,server_address,time" );
insert into table MSS_SERVICE_CONTEXT_FINAL select service_class, meta_server_addess as server_addess, count(request_uri) as avg_request_count, service_context, substring(cast(meta_timestamp/1000 as timestamp),0,16) as time, facet2(service_class,server_address) from ServiceRequestData group by meta_server_addess, service_class, service_context, substring(cast(meta_timestamp/1000 as timestamp),0,16);
